<template>
  <div>
    <div>
      <el-row class="dw-lr-header" >
        <el-col :span="24" >
          <div class="dw-header-main" >
            <el-row type="flex" justify="space-between" align="middle">
              <el-col :span="18" >
                <div class="header-content-left">
                  <div class="logo">
                    <router-link :to="`${prevPath}/dw/survey`" >
                      <img src="data:image/png;base64," alt="logo" height="32">
                      <h1>调问网</h1>
                      <span style="color: #ffa71d;font-size: 14px;position: absolute;margin-left: 10px;">Bus</span>
                    </router-link>
                  </div>
                  <!--                  <div><dw-lr-nav-menu></dw-lr-nav-menu></div>-->
                </div>
              </el-col>
              <el-col :span="6" style="text-align: right;padding-right: 10px;" >
                <div class="dw-display-flex-right">
                  <div style="color: #c2a20c;padding-right: 20px;font-size: 14px;cursor: pointer;" @click="openSwitchLayoutDialog">
                    <i class="fa-solid fa-toggle-on"></i> 切换布局
                  </div>
                  <el-dropdown @command="handleCommand">
                    <span class="el-dropdown-link" >
                      {{ userName }} <i class="el-icon-arrow-down el-icon--right"></i>
                    </span>
                    <el-dropdown-menu slot="dropdown">
                      <el-dropdown-item command="myAccount">我的账号</el-dropdown-item>
                      <el-dropdown-item command="updatePwd">修改密码</el-dropdown-item>
                      <el-dropdown-item command="logout">退出登录</el-dropdown-item>
                    </el-dropdown-menu>
                  </el-dropdown>
                </div>
              </el-col>
            </el-row>
          </div>
        </el-col>
      </el-row>
      <switch-layout-dialog ref="switchLayoutDialog"></switch-layout-dialog>
    </div>
  </div>
</template>

<script>
import DwAuthorized from '@/utils/dw-authorized'
import {logout} from '@/api/dw-login'
import DwNavMenu from '../DwNavMenu'
import DwLrNavMenu from './DwLrNavMenu.vue'
import SwitchLayoutDialog from '../components/SwitchLayoutDialog.vue'

export default {
  name: 'DwLrHeader',
  components: {
    SwitchLayoutDialog,
    DwLrNavMenu,
    DwNavMenu
  },
  data () {
    return {
      userName: 'dwsurvey',
      prevPath: '/v6/lr'
    }
  },
  mounted () {
    this.userName = DwAuthorized.getUserName()
    const routePath = this.$route.path
    if (routePath.indexOf('/v6/lr') >= 0) {
      this.prevPath = '/v6/lr'
    }
  },
  methods: {
    handleCommand: function (command) {
      if (command === 'logout') {
        logout().then(() => {
          this.$router.push('/login')
        })
      } else if (command === 'myAccount') {
        this.$router.push(`${this.prevPath}/dw/user`)
      } else if (command === 'updatePwd') {
        this.$router.push(`${this.prevPath}/dw/user/pwd`)
      }
    },
    openSwitchLayoutDialog () {
      this.$refs.switchLayoutDialog.openDialog()
    }
  }
}
</script>

